.car {
    width: 285px;
    height: 125px;
    background: #b6bec3;
    position: relative;
    display: inline-block;
    vertical-align: middle;
    *vertical-align: auto;
    *zoom: 1;
    *display: inline;
    -moz-border-radius-topright: 40px 50%;
    -webkit-border-top-right-radius: 40px 50%;
    border-top-right-radius: 40px 50%;
    -moz-border-radius-bottomright: 40px 50%;
    -webkit-border-bottom-right-radius: 40px 50%;
    border-bottom-right-radius: 40px 50%;
    -moz-border-radius-bottomleft: 20px 30px;
    -webkit-border-bottom-left-radius: 20px 30px;
    border-bottom-left-radius: 20px 30px;
    -moz-border-radius-topleft: 20px 30px;
    -webkit-border-top-left-radius: 20px 30px;
    border-top-left-radius: 20px 30px;
}

.car:after {
    top: -12px;
    -moz-border-radius: 3px 100% 100% 3px;
    -webkit-border-radius: 3px 100% 100% 3px;
    border-radius: 3px 100% 100% 3px;
    -moz-transform: rotate(-32deg);
    -ms-transform: rotate(-32deg);
    -webkit-transform: rotate(-32deg);
    transform: rotate(-32deg);
}

.car:before {
    bottom: -10px;
    -moz-border-radius: 100% 3px 3px 100%;
    -webkit-border-radius: 100% 3px 3px 100%;
    border-radius: 100% 3px 3px 100%;
    -moz-transform: rotate(32deg);
    -ms-transform: rotate(32deg);
    -webkit-transform: rotate(32deg);
    transform: rotate(32deg);
}

